`year_month` = #{yearMonth,jdbcType=DATE}, product_num = #{productNum}, product_name = #{productName}, summary = #{summary}, person_month = #{personMonth}, order_amount = #{orderAmount}, remark = #{remark}, update_time = now(), update_by = #{updateBy}
WHERE month_id = #{monthId}

update.foreach:
UPDATE basic_v2_month
project_technician_id = #{item.projectTechnicianId}, presumed_time = #{item.presumedTime}, expected_price = #{item.expectedPrice}, actual_hours = #{item.actualHours}, actual_price = #{item.actualPrice}, `from` = #{item.from}, actual_price_edit = #{item.actualPriceEdit}, `from_edit` = #{item.fromEdit}, subcontract_price = #{item.subcontractPrice}, `year_month` = #{item.yearMonth,jdbcType=DATE}, product_num = #{item.productNum}, product_name = #{item.productName}, summary = #{item.summary}, person_month = #{item.personMonth}, order_amount = #{item.orderAmount}, remark = #{item.remark}, update_time = now(), update_by = #{item.updateBy}
WHERE month_id = #{item.monthId}
